function longestSubstringWithoutRepeats(str) {
  let longest = '';
  let seen = new Set();
  let start = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str[i];
    if (!seen.has(char)) {
      seen.add(char);
      longest = longest.length > seen.size ? longest : str.slice(start, i + 1);
    } else {
      while (str[start] !== char) {
        seen.delete(str[start]);
        start++;
      }
      start++;
      seen.clear();
    }
  }
  return longest;
}

